mapper.select:

SELECT * FROM (
    SELECT Tb.*, rownum rNum FROM (
        SELECT * FROM member 

			WHERE ${sField} LIKE '%'||#{searchKeyword}||'%'

        ORDER BY midx DESC
    ) Tb
)
WHERE rNum>=#{start} AND rNum<=#{end}


SELECT * FROM (
    SELECT Tb.*, rownum rNum FROM (
        SELECT A.*, M.m_name FROM apply A INNER JOIN member M ON A.midx=M.midx

			WHERE ${sField} LIKE '%'||#{searchKeyword}||'%'

        ORDER BY apply_idx DESC
    ) Tb
)
WHERE rNum>=#{start} AND rNum<=#{end}
