mapper.select:
SELECT * FROM ( SELECT Tb.*, rownum rNum FROM ( SELECT * FROM product where auction=0
and title LIKE '%' || #{str} || '%' OR m_name LIKE '%' || #{str} || '%'
and price BETWEEN #{pmin} AND #{pmax}
and sold = 1 and sold = 0 and size1=size2 and size1 > size2 and size1 < size2 and size2 BETWEEN 0 and 15 and size2 BETWEEN 15 and 25 and size2 BETWEEN 25 and 50 and size2 BETWEEN 50 and 100 and size2 > 100 and p_type='아크릴' and p_type='유화' and p_type='수채화' and p_type='수묵화' and p_type='스케치' and p_type='혼합/기타' and theme='자연/풍경' and theme='인물' and theme='동식물' and theme='사물' and theme='추상' and theme='기타'
ORDER BY p_like DESC ORDER BY price ORDER BY price DESC ORDER BY pidx DESC
) Tb) WHERE rNum BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT Tb.*, rownum rNum FROM ( select * from product where auction=1
and title LIKE '%' || #{str} || '%' OR m_name LIKE '%' || #{str} || '%'
and price BETWEEN #{pmin} AND #{pmax}
and enddate < sysdate and enddate > sysdate and size1 = size2 and size1 > size2 and size1 < size2 and size2 BETWEEN 0 and 15 and size2 BETWEEN 15 and 25 and size2 BETWEEN 25 and 50 and size2 BETWEEN 50 and 100 and size2 > 100 and p_type='아크릴' and p_type='유화' and p_type='수채화' and p_type='수묵화' and p_type='스케치' and p_type='혼합/기타' and theme='자연/풍경' and theme='인물' and theme='동식물' and theme='사물' and theme='추상' and theme='기타'
ORDER BY p_like DESC ORDER BY price ORDER BY price DESC ORDER BY pidx DESC
) Tb) WHERE rNum BETWEEN #{start} AND #{end}

SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM review WHERE aidx = #{aidx}
	) Tb) WHERE rNum BETWEEN #{start} AND #{end}

SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM product WHERE aidx=#{aidx} AND sold=#{sold}
	) Tb) WHERE rNum BETWEEN #{start} AND #{end}

select order2.o2idx, order1.oidx, order1.midx, order1.paymethod, order1.price as tprice, order1.orderdate, order1.paydate, order1.shipdate, order1.courier, order1.t_num, order1.receiver, order1.r_phone, order1.r_address, order1.message, order1.owner, order1.auction, order1.add_point, order1.minus_point, product.pidx, product.sfile, product.title, product.m_name, product.size1, product.size2, product.price, product.ridx from order2 inner join product on order2.pidx = product.pidx inner join order1 on order1.oidx = order2.oidx where order1.midx=#{midx}
and order1.paydate is null and order1.shipdate is null and order1.paydate is not null and order1.shipdate is null and order1.paydate is not null and order1.shipdate is not null
and order1.orderdate BETWEEN #{dmin} AND #{dmax}
and product.title LIKE '%' || #{str} || '%' OR product.m_name LIKE '%' || #{str} || '%'

select product.*, auction2.aprice, auction2.aucdate, auction2.id from product inner join auction2 on product.pidx = auction2.pidx where auction2.id=#{id}


		 and auction2.aucdate BETWEEN #{dmin} AND #{dmax}

	  order by auction2.aucdate desc
